 import React, { useRef, useState } from "react";

//Voisi valita url ajan kirjoittamalla sen panikkeisiin, voi olla placeholder aika esimerkkinä
//Käy for loopilla kerran läpi json ja merkkaa siinä arvot


function App() {
  const baseURL = "https://api.coingecko.com/api/v3/coins/bitcoin/market_chart";

  const get_id = useRef(null);
  const get_title = useRef(null);

  const [getResult, setGetResult] = useState(null);

  const fortmatResponse = (res) => {
    return JSON.stringify(res, null, 2);
  }

  const input = props => {  
    const [textInput, setTextInput] = React.useState('');

    const handleClick = () => {
      console.log(textInput);
      props.send(textInput);
    }

    const handleChange = (event) => {
      setTextInput(event.target.value);
    }
  }  

  async function getAllData() {
    try {

      //Tee tähä päivämäärä muuttujat 
      //const start_date
      //const end_date
      //funktio, unixtime -> date-time

      const res = await fetch(`${baseURL}/range?vs_currency=eur&from=1577836800&to=1609376400`);

      if (!res.ok) {
        const message = `An error has occured: ${res.status} - ${res.statusText}`;
        throw new Error(message);
      }

      const data = await res.json();

      Object.values(data).forEach(value => {
        console.log(value);
     });

      setGetResult(fortmatResponse(data));
    } catch (err) {
      setGetResult(err.message);
    }
  }


  return (
    <div className="card">
      <div className="card-header">Get longest downtrend, date of highest volume, and maximal profit window</div>
      <div className="card-body">
        <div className="input-group input-group-sm">
          <button className="btn btn-sm btn-primary" onClick={getAllData}>Get All</button>
          <input type="text" ref={get_id} className="form-control ml-2" placeholder="10.1.2021" />
          <input type="text" ref={get_title} className="form-control ml-2" placeholder="20.1.2021" />
        </div>   
        { getResult && <div className="alert alert-secondary mt-2" role="alert"><pre>{getResult}</pre></div> }
      </div>
    </div>
  );
}

export default App;
