import React, { useState } from "react"
import Header from "./components/Header"
import ShowHighestVolume from "./components/ShowHighestVolume"
import ShowLongestDownTrend from "./components/ShowLongestDownTrend"
import ShowMaxProfitWindow from "./components/ShowMaxProfitWindow"

//Käy for loopilla kerran läpi json ja merkkaa siinä arvot
//1-90pv hourly data eli laske päivien määrä datedatasta ja sitten jaa saatu tulos ota 24 tunnin
//sisältä volyymien summa ja hinnan keskiarvo

//yli 90pv daily data
//
function App() {
  const [getResult, setGetResult] = useState(null);  
  const [getStartDate, setStartDate] = useState(null);  
  const [getEndDate, setEndDate] = useState(null);  


  const fortmatResponse = (res) => {
    return JSON.stringify(res, null, 2);}

  // Fetch Tasks
  const fetchTasks = async (e) => {
    e.preventDefault()
    const startunixtime = (Date.parse(getStartDate)/1000)-2*3600
    const endunixtime = (Date.parse(getEndDate)/1000)-3600    
    const res = await fetch("https://api.coingecko.com/api/v3/coins/bitcoin/market_chart/range?vs_currency=eur&from="+startunixtime+"&to="+endunixtime)
    const data = await res.json()
    console.log(startunixtime)
    console.log(endunixtime)   
    //tarkista jotenkin että saa oikean unixtimen
    //Date.parsesta käännä takaisin päiväksi?
    console.log("INPUT START DATE")
    console.log(getStartDate)
    console.log("UNIX TO INPUT START DATE")
    const milliseconds1 = startunixtime * 1000 // 1575909015000
    const dateObject1 = new Date(milliseconds1)
    const humanDateFormat1 = dateObject1.toLocaleString() //2019-12-9 10:30:15
    console.log(humanDateFormat1)

    console.log("INPUT END DATE")
    console.log(getEndDate)
    console.log("UNIX TO INPUT END DATE")
    const milliseconds = (endunixtime) * 1000  // 1575909015000
    const dateObject = new Date(milliseconds)
    const humanDateFormat = dateObject.toLocaleString() //2019-12-9 10:30:15
    console.log(humanDateFormat)

    console.log(data)
    setGetResult(fortmatResponse(data));
  }  



  return (

    <div className="container">
      <Header />      
      <form className='add-form' onSubmit={fetchTasks}>
      <div className='form-control'>
                <label>Start date: </label>
                <input type='date' 
                onChange={(e) => setStartDate(e.target.value)}
                />

          </div>
          <div className='form-control'>
                <label>End date: </label>
                <input type='date' 
                onChange={(e) => setEndDate(e.target.value)}/>
          </div>
          <input type='submit' value='Load data'
          className='btn btn-block'/>
     </form>
      <ShowLongestDownTrend data={getResult}/>
      <ShowHighestVolume />
      <ShowMaxProfitWindow />
    </div>
  );
}

export default App;
