import React, { useState } from "react"
import Header from "./components/Header"
import ShowHighestVolume from "./components/ShowHighestVolume"
import ShowLongestDownTrend from "./components/ShowLongestDownTrend"
import ShowMaxProfitWindow from "./components/ShowMaxProfitWindow"

//Käy for loopilla kerran läpi json ja merkkaa siinä arvot
//1-90pv hourly data eli laske päivien määrä datedatasta ja sitten jaa saatu tulos ota 24 tunnin

//if daycount <= 90,  
//daycount = getEndDate-getStartDate
//0-24 rivi sisältä volyymien summa ja hinnan keskiarvo. 

//yli 90pv daily data

//Älä laske viimeistä tuntia mukaan
//C) Aikaikkuna jonka loppupäätä liikutetaan alkuun korkeimpaan kohtaan, sitten alkupäätä matalimpaan.
//Jos korkeampaa kohtaa ei löydy niin jää korkeaan. Etsi vielä matalampaa. Tämä alue on tutkittu.


function App() {
  const [data, setData] = useState(null);  
  const [startDate, setStartDate] = useState(null);  
  const [endDate, setEndDate] = useState(null);  

  // Fetch Tasks
  const fetchData = async (e) => {
    e.preventDefault()
    const startunixtime = (Date.parse(startDate)/1000)-2*3600
    const endunixtime = (Date.parse(endDate)/1000)-3600    
    const res = await fetch("https://api.coingecko.com/api/v3/coins/bitcoin/market_chart/range?vs_currency=eur&from="+startunixtime+"&to="+endunixtime)
    const dataset = await res.json()

    console.log("UNIX TO INPUT START DATE")
    const milliseconds1 = 1577836800000  // 1575909015000
    const dateObject1 = new Date(milliseconds1)
    const humanDateFormat1 = dateObject1.toLocaleString() //2019-12-9 10:30:15
    console.log(humanDateFormat1)

    console.log(dataset)
    setData(dataset);
  }  


  return (

    <div className="container">
      <Header />      
      <form className='add-form' onSubmit={fetchData}>
      <div className='form-control'>
                <label>Start date: </label>
                <input type='date' 
                onChange={(e) => setStartDate(e.target.value)}
                />

          </div>
          <div className='form-control'>
                <label>End date: </label>
                <input type='date' 
                onChange={(e) => setEndDate(e.target.value)}/>
          </div>
          <input type='submit' value='Load data'
          className='btn btn-block'/>
     </form>
      <ShowLongestDownTrend data={data}/>
      <ShowHighestVolume />
      <ShowMaxProfitWindow />
    </div>
  );
}

export default App;
