import React, { useState } from "react"
import Header from "./components/Header"
import ShowHighestVolume from "./components/ShowHighestVolume"
import ShowLongestDownTrend from "./components/ShowLongestDownTrend"
import ShowMaxProfitWindow from "./components/ShowMaxProfitWindow"
//Voisi valita url ajan kirjoittamalla sen panikkeisiin, voi olla placeholder aika esimerkkinä
//Käy for loopilla kerran läpi json ja merkkaa siinä arvot


function App() {
  const [getResult, setGetResult] = useState(null);  
  const [getStartDate, setStartDate] = useState(null);  
  const [getEndDate, setEndDate] = useState(null);  
  //Input painikkeet päivämäärille jotka välitetään fetchTaskille


  const fortmatResponse = (res) => {
    return JSON.stringify(res, null, 2);}

  // Fetch Tasks
  const fetchTasks = async (e) => {
    e.preventDefault()


    const startunixtime = Date.parse(getStartDate)/1000
    const endunixtime = Date.parse(getEndDate)/1000
    console.log(startunixtime)
    console.log(endunixtime)
    console.log(getStartDate)
    console.log(getEndDate)

    const res = await fetch("https://api.coingecko.com/api/v3/coins/bitcoin/market_chart/range?vs_currency=eur&from="+startunixtime+"&to="+endunixtime)
    const data = await res.json()

    console.log(data)

    setGetResult(fortmatResponse(data));
  }  


  return (

    <div className="container">
      <Header />      
      <form className='add-form' onSubmit={fetchTasks}>
      <div className='form-control'>
                <label>Start date: </label>
                <input type='date' 
                onChange={(e) => setStartDate(e.target.value)}
                />

          </div>
          <div className='form-control'>
                <label>End date: </label>
                <input type='date' 
                onChange={(e) => setEndDate(e.target.value)}/>
          </div>
          <input type='submit' value='Load data'
          className='btn btn-block'/>
      </form>
      <ShowLongestDownTrend data={getResult}/>
      <ShowHighestVolume />
      <ShowMaxProfitWindow />
    </div>
  );
}

export default App;
