import React, { useState } from "react"
import Header from "./components/Header"
import ShowHighestVolume from "./components/ShowHighestVolume"
import ShowLongestDownTrend from "./components/ShowLongestDownTrend"
import ShowMaxProfitWindow from "./components/ShowMaxProfitWindow"
import { biggestvolume } from "./modules/BiggestVolume"
import { hourstodays } from './modules/HoursToDays';

//1-90pv eli ota lähimpänä UTC tai +2GMT oleva aika eli lähimpänä 2:00:0 AM, siitä volyymi ja hinta

function App() {
  const [startDate, setStartDate] = useState(null);  
  const [endDate, setEndDate] = useState(null);  
  const [volume, setVolume] = useState(null);

  // Fetch Tasks
  const fetchData = async (e) => {
    e.preventDefault()
    const startunixtime = ((Date.parse(startDate))/1000)
    const endunixtime = ((Date.parse(endDate))/1000)+3600
    const res = await fetch("https://api.coingecko.com/api/v3/coins/bitcoin/market_chart/range?vs_currency=eur&from="+startunixtime+"&to="+endunixtime)
    var dataset = await res.json() //mahd toinen kuin json()

    var diff = Math.abs(endunixtime*1000- startunixtime*1000);
    var daycount = (diff/86400000)

    if (daycount <= 90){
      console.log("alle 90 päivää")
      dataset = hourstodays(dataset)
    }
    else{
      console.log("yli 90 päivää")
    }

    console.log(dataset)
    setVolume(biggestvolume(dataset))

    //Tehtävä A
    //

  }  


  return (

    <div className="container">
      <Header />      
      <form className='add-form' onSubmit={fetchData}>
      <div className='form-control'>
                <label>Start date: </label>
                <input type='date' 
                onChange={(e) => setStartDate(e.target.value)}
                />

          </div>
          <div className='form-control'>
                <label>End date: </label>
                <input type='date' 
                onChange={(e) => setEndDate(e.target.value)}/>
          </div>
          <input type='submit' value='Load data'
          className='btn btn-block'/>
     </form>

      <ShowLongestDownTrend/>
      <ShowHighestVolume/>
      <h3 style={{ color: 'steelblue', backgroundColor:'black'  }}>Date: {volume[0]} GMT +0200</h3>
      <h3 style={{ color: 'steelblue', backgroundColor:'black'  }}>Volume: {volume[1]} €</h3>
      <ShowMaxProfitWindow />
    </div>
  );
}

export default App;


