//Älä laske viimeistä tuntia mukaan
//C) Aikaikkuna jonka loppupäätä liikutetaan alkuun korkeimpaan kohtaan, sitten alkupäätä matalimpaan.
//Jos korkeampaa kohtaa ei löydy niin jää korkeaan. Etsi vielä matalampaa. Tämä alue on tutkittu.

import { biggestvolume } from "./BiggestVolume"

//Iskän idea eli ota loppupäästä korkein, sitten alkupäästä käy läpi, löytyy korkein profit. 
//Sitten siirrä looppupäätä alkuunpäin jos korkeampi löytyy, ja taas alusta hae matalinta

export function maximalprofitwindow(dataset) {

    //loppupää lopusta alkuun , pysähdy aina uuden korkeimman huipun kohdalle kunnes alussa.
    //Kun korkein huippu niin mene alkupään kokeilulla alusta loppuun ja pysähdy alimmassa, merkkaa parhaaksi 
    //tulokseksi
    var datalenght = Object.keys(dataset["total_volumes"]).length;
    //var last_window_end = datalenght
    var day_lowest_price = null
    var day_highest_price = null
    var highest_price = 0
    var lowest_price = 100000
    var global_lowest_price = 100000
    var global_day_lowest_price = null
    var global_highest_price = 0
    var global_day_highest_price = null
    var biggest_profit = 0



    for (var i = datalenght-1; i >= 0; i--) {

        if (dataset["prices"][i][1] > highest_price  ){
            highest_price = dataset["prices"][i][1]
            day_highest_price = dataset["prices"][i][0]
            //tule alusta tähän pisteeseen etsien alinta kohtaa
            for (var z = 0; z < i; z++){

                if (dataset["prices"][z][1] < lowest_price  ){ 
                    lowest_price = dataset["prices"][z][1]
                    day_lowest_price = dataset["prices"][z][0]
                    console.log("Lowest day")
                    console.log(dataset["prices"][z][0])
            }
            //Nollaa lowest_price ja pistä kuitenkin global lowest_price muistiin
            //vertaaconsole.log(highest_price-lowest_price)
            //jos suurempi profit niin sitten talleta globaaleiksi


            if (biggest_profit < (highest_price-lowest_price)){
                biggest_profit = highest_price-lowest_price
                console.log("profit")
                console.log(biggest_profit)
                global_lowest_price = lowest_price
                global_highest_price = highest_price
                global_day_lowest_price = day_lowest_price
                global_day_highest_price = day_highest_price
                console.log("GLOBAL DAY")
                console.log(global_day_lowest_price)
                console.log(day_lowest_price)
                console.log(global_lowest_price)

            }   
            lowest_price = 10000
            day_lowest_price = null
            //ota ylin-alin hinta ja niiden päivämäärät
            //jos ylin-alin on negatiivinen niin ei kuulu ostaa tai myydä tai jos null
        }
    }


}

